import { useEffect, useState } from "react";

export default function App() {
  const [cart, setCart] = useState([]);
  const [lpgPrice, setLpgPrice] = useState(null);
  const [headerImage, setHeaderImage] = useState("/header.webp"); // domyślny baner

  useEffect(() => {
    fetch("/data.json")
      .then((res) => res.json())
      .then((data) => setLpgPrice(data.lpgPrice))
      .catch((err) => console.error("Błąd wczytywania ceny LPG:", err));
  }, []);

  const products = [
    { id: 2, name: "Gaz w butli 11KG Propan-Butan", price: 75, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-11kg.png" },
    { id: 3, name: "Gaz w butli 10KG Propan-Butan", price: 85, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-11kg.png" },
    { id: 4, name: "Gaz w butli 8KG Propan-Butan", price: 62, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-8kg.jpg" },
    { id: 5, name: "Gaz w butli 5KG", price: 53, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-5kg.jpg"  },
    { id: 6, name: "Gaz w butli 3KG", price: 45, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-3kg.jpg"  },
    { id: 7, name: "Gaz w butli 2KG", price: 40, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-2kg.jpg" },
    { id: 8, name: "Gaz w butli 1KG", price: 27, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-1kg.jpg"  },
    { id: 9, name: "Pojemnik 11KG", price: 260, desc: "Cena za pustą butlę (bez gazu).", image: "/butla-11kg.png" },
    { id: 10, name: "Sodastream Nabój", price: 15,desc: "Cena za wymianę butli Click On lub Twist On", image: "/SodaStream.jpg" },
    { id: 11, name: "Płyn do spryskiwaczy letni", price: 11, desc: "Opakowanie 5L. Gotowy do użycia.", image: "/Płyn-lato.jpg"  },
    { id: 12, name: "Płyn do spryskiwaczy zimowy", price: 22, desc: "Opakowanie 5L. Odporny na mróz do -20 stopni.", image: "/Płyn-zima.jpg"  },
  ];

  // Dodawanie do koszyka
  const addToCart = (product) => {
    const existing = cart.find((item) => item.id === product.id);
    if (existing) {
      setCart(
        cart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        )
      );
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  // Usuwanie
  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  // Zmiana ilości
  const updateQuantity = (id, delta) => {
    setCart(
      cart.map((item) =>
        item.id === id
          ? { ...item, quantity: Math.max(1, item.quantity + delta) }
          : item
      )
    );
  };

  // Podsumowanie
  const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  return (
    <div className="min-h-screen bg-blue-50 text-gray-800">
      {/* Header */}
      <header className="relative text-center mb-10">
        <img src="/banner.jpg" alt="Baner" className="w-full h-64 object-cover rounded-b-2xl shadow" />
        <div className="absolute inset-0 flex flex-col items-center justify-center text-white bg-black/40">
          <h1 className="text-5xl font-bold">Twoja Stacja LPG</h1>
          <p className="text-xl mt-2">Ulica Podhalańska 25, 44-335 Jastrzębie-Zdrój</p>
          <p className="text-lg font-semibold mt-1">
            Cena LPG: {lpgPrice ? `${lpgPrice} zł / L` : "Ładowanie..."}
          </p>
        </div>
      </header>

      {/* Sklep */}
      <main className="container mx-auto grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 p-6">
        {products.map((product) => (
          <div key={product.id} className="bg-white rounded-2xl shadow p-6 flex flex-col justify-between border-t-4 border-yellow-400">
            {product.image && <img src={product.image} alt={product.name} className="h-40 mx-auto mb-4 object-contain" />}
            <h2 className="text-xl font-semibold mb-2 text-center">{product.name}</h2>
            <p className="text-lg mb-1 text-center">{product.price.toFixed(2)} zł</p>
            {product.desc && <p className="text-sm text-gray-600 mb-4 text-center">{product.desc}</p>}
            <button onClick={() => addToCart(product)} className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 mt-auto">Dodaj do koszyka</button>
          </div>
        ))}
      </main>

      {/* Koszyk */}
      <aside className="mt-10 bg-white p-6 rounded-2xl shadow max-w-xl mx-auto">
        <h2 className="text-2xl font-bold mb-4">🛒 Koszyk</h2>
        {cart.length === 0 ? (
          <p className="text-gray-500">Koszyk jest pusty</p>
        ) : (
          <ul className="mb-4">
            {cart.map((item) => (
              <li key={item.id} className="flex justify-between items-center border-b py-2">
                <span>{item.name}</span>
                <div className="flex items-center gap-2">
                  <button onClick={() => updateQuantity(item.id, -1)} className="px-2 py-1 bg-gray-200 rounded">-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => updateQuantity(item.id, 1)} className="px-2 py-1 bg-gray-200 rounded">+</button>
                  <span>{(item.price * item.quantity).toFixed(2)} zł</span>
                  <button onClick={() => removeFromCart(item.id)} className="ml-2 text-red-600">✕</button>
                </div>
              </li>
            ))}
          </ul>
        )}
        <p className="font-semibold text-lg">Razem: {total.toFixed(2)} zł</p>
      </aside>

      {/* Program lojalnościowy */}
      <section className="container mx-auto my-16 text-center">
        <h2 className="text-3xl font-bold mb-6 text-blue-700">Dołącz do programu</h2>
        <p className="mb-8">Zbieraj podpisy przy każdej wymianie butli lub tankowaniu LPG. <br />
        Po zebraniu kompletu — rabat 50% na następną butlę lub stały rabat przy LPG.</p>
        <div className="flex justify-center gap-6 flex-wrap">
          <img src="/karta1.jpg" alt="Karta rabatowa 1" className="w-48 rounded-xl shadow" />
          <img src="/karta2.jpg" alt="Karta rabatowa 2" className="w-48 rounded-xl shadow" />
          <img src="/ulotka.jpg" alt="Ulotka rabatowa" className="w-40 rounded-xl shadow" />
        </div>
      </section>

      {/* Informacje dodatkowe */}
      <section className="container mx-auto my-16 text-center bg-yellow-100 p-8 rounded-2xl">
        <h2 className="text-2xl font-bold mb-4">ℹ️ Dodatkowe informacje</h2>
        <p>🚚 Dostawa butli pod wskazany adres: 20 zł (tel. 123-456-789)</p>
        <p>🔗 Wypożyczenie przyczepki: do 3h — 20 zł, powyżej 3h — 50 zł/doba</p>
        <p>🛠️ Planujemy rozszerzenie asortymentu o akcesoria do butli i LPG</p>
