import { useEffect, useState } from "react";

export default function App() {
  const [cart, setCart] = useState([]);
  const [lpgPrice, setLpgPrice] = useState(null);
  const [headerImage, setHeaderImage] = useState("/header.webp"); // domyślny baner

  useEffect(() => {
    fetch("/data.json")
      .then((res) => res.json())
      .then((data) => setLpgPrice(data.lpgPrice))
      .catch((err) => console.error("Błąd wczytywania ceny LPG:", err));
  }, []);

  const products = [
    { id: 2, name: "Gaz w butli 11KG Propan-Butan", price: 75, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-11kg.png" },
    { id: 3, name: "Gaz w butli 10KG Propan-Butan", price: 85, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-11kg.png" },
    { id: 4, name: "Gaz w butli 8KG Propan-Butan", price: 62, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-8kg.jpg" },
    { id: 5, name: "Gaz w butli 5KG", price: 53, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-5kg.jpg"  },
    { id: 6, name: "Gaz w butli 3KG", price: 45, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-3kg.jpg"  },
    { id: 7, name: "Gaz w butli 2KG", price: 40, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-2kg.jpg" },
    { id: 8, name: "Gaz w butli 1KG", price: 27, desc: "Cena za wymianę lub napełnienie. Wymagana własna butla.", image: "/butla-1kg.jpg"  },
    { id: 9, name: "Pojemnik 11KG", price: 260, desc: "Cena za pustą butlę (bez gazu).", image: "/butla-11kg.png" },
    { id: 10, name: "Sodastream Wymiana", price: 15,desc: "Cena za wymianę butli Click On lub Twist On", image: "/SodaStream.jpg" },
    { id: 11, name: "Płyn do spryskiwaczy letni", price: 11, desc: "Opakowanie 5L. Zapach Zielonego Jabłka.", image: "/Płyn-lato.jpg"  },
    { id: 12, name: "Płyn do spryskiwaczy zimowy", price: 22, desc: "Opakowanie 5L. Odporny na mróz do -20 stopni. Zapach Cytrusowy", image: "/Płyn-zima.jpg"  },
  ];

  const addToCart = (product) => {
    setCart((prev) => {
      const found = prev.find((p) => p.id === product.id);
      if (found) {
        return prev.map((p) =>
          p.id === product.id ? { ...p, quantity: p.quantity + 1 } : p
        );
      }
      // zawsze inicjujemy quantity = 1
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const updateQuantity = (id, delta) => {
    setCart((prev) =>
      prev
        .map((item) =>
          item.id === id ? { ...item, quantity: Math.max(1, (item.quantity || 1) + delta) } : item
        )
        .filter((i) => i.quantity > 0)
    );
  };

  const removeFromCart = (id) => {
    setCart((prev) => prev.filter((item) => item.id !== id));
  };

  const total = cart.reduce(
    (acc, item) => acc + Number(item.price) * Number(item.quantity || 1),
    0
  );
  // Formularz
  const [customerType, setCustomerType] = useState("osoba"); // 'osoba' | 'firma'
  const [form, setForm] = useState({
    name: "",
    phone: "",
    email: "",
    address: "",
    companyName: "",
    nip: "",
    payment: "Gotówka przy odbiorze", // domyślna
  });

  const handleForm = (e) => setForm((f) => ({ ...f, [e.target.name]: e.target.value }));

  const submitOrder = async (e) => {
    e.preventDefault();
    if (cart.length === 0) return;

    const payload = {
      cart,
      total: Number(total.toFixed(2)),
      customerType,
      form,
    };

    try {
      const res = await fetch("http://localhost:5000/api/order", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      if (!res.ok) throw new Error("Błąd HTTP");

      alert("Zamówienie wysłane. Dziękujemy!");
      setCart([]);
      setCustomerType("osoba");
      setForm({
        name: "",
        phone: "",
        email: "",
        address: "",
        companyName: "",
        nip: "",
        payment: "Gotówka przy odbiorze",
      });
    } catch (err) {
      console.error(err);
      alert("Nie udało się wysłać zamówienia. Sprawdź backend i połączenie.");
    }
  };

  return (
    <div className="min-h-screen bg-blue-50 text-gray-900">
      {/* Pasek górny */}
      <header className="bg-blue-600 text-white p-4 shadow-md">
        <div className="container mx-auto flex flex-wrap items-center justify-between gap-4">
          <h1 className="text-2xl font-bold">Twoja Stacja LPG</h1>
          <nav className="flex gap-6">
            <a href="#sklep" className="hover:text-yellow-300">Sklep</a>
            <a href="#karta" className="hover:text-yellow-300">Karta stałego klienta</a>
            <a href="#przyczepka" className="hover:text-yellow-300">Przyczepka</a>
            <a href="#kontakt" className="hover:text-yellow-300">Kontakt</a>
          </nav>
        </div>
      </header>

      {/* Stały baner */}
<header className="relative w-full h-64 md:h-80 lg:h-96">
  <img
    src="/header.webp" 
    alt="Baner Stacji LPG"
    className="w-full h-full object-cover"
  />
  <div className="absolute inset-0 bg-black bg-opacity-40 flex flex-col justify-center items-center text-center text-white">
    <h1 className="text-4xl md:text-5xl font-bold mb-4">
      Twoja Stacja LPG
    </h1>
    <p className="text-lg md:text-xl font-semibold">
      Ulica Podhalańska 25, 44-335 Jastrzębie-Zdrój
    </p>
    <p className="text-md md:text-lg mt-2 text-yellow-400">
      Cena LPG: {lpgPrice ? `${lpgPrice} zł / L` : "Ładowanie..."}
    </p>
  </div>
</header>

      {/* Sklep */}
      <main className="container mx-auto grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {products.map((product) => (
          <div key={product.id} className="bg-white rounded-2xl shadow p-6 flex flex-col">
            {product.image && (
              <img
                src={product.image}
                alt={product.name}
                className="h-40 w-auto mx-auto mb-4 object-contain"
              />
            )}
            <h2 className="text-xl font-semibold mb-1 text-center">{product.name}</h2>
            <p className="text-lg mb-2 text-center">{Number(product.price).toFixed(2)} zł</p>
            {product.desc && (
              <p className="text-sm text-gray-600 mb-4 text-center">{product.desc}</p>
            )}
            <button
              onClick={() => addToCart(product)}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 mt-auto"
            >
              Dodaj do koszyka
            </button>
          </div>
        ))}
      </main>

      {/* KOSZYK */}
      <aside className="mt-10 bg-white p-6 rounded-2xl shadow max-w-3xl mx-auto">
        <h2 className="text-2xl font-bold mb-4 flex items-center gap-2">
          <span role="img" aria-label="cart">🛒</span> Koszyk
        </h2>

        {cart.length === 0 ? (
          <p className="text-gray-500">Koszyk jest pusty</p>
        ) : (
          <>
            <ul className="mb-4 divide-y">
              {cart.map((item) => (
                <li key={item.id} className="py-3 flex justify-between items-center">
                  <span className="mr-2">{item.name}</span>
                  <div className="flex items-center gap-2">
                    <button
                      onClick={() => updateQuantity(item.id, -1)}
                      className="px-3 py-1 bg-gray-200 rounded"
                    >
                      -
                    </button>
                    <span className="w-6 text-center">{item.quantity}</span>
                    <button
                      onClick={() => updateQuantity(item.id, 1)}
                      className="px-3 py-1 bg-gray-200 rounded"
                    >
                      +
                    </button>
                    <span className="w-24 text-right">
                      {(Number(item.price) * Number(item.quantity || 1)).toFixed(2)} zł
                    </span>
                    <button
                      onClick={() => removeFromCart(item.id)}
                      className="ml-2 text-red-600 hover:underline"
                      title="Usuń"
                    >
                      ✖
                    </button>
                  </div>
                </li>
              ))}
            </ul>
            <p className="font-semibold text-lg">Razem: {total.toFixed(2)} zł</p>
          </>
        )}
      </aside>

      {/* FORMULARZ ZAMÓWIENIA – pokazujemy tylko gdy coś jest w koszyku */}
      {cart.length > 0 && (
        <form
          onSubmit={submitOrder}
          className="mt-8 bg-white p-6 rounded-2xl shadow max-w-3xl mx-auto"
        >
          <h3 className="text-2xl font-bold mb-4">Dane zamówienia</h3>

          {/* Typ klienta */}
          <div className="flex gap-6 mb-4">
            <label className="cursor-pointer">
              <input
                type="radio"
                name="ctype"
                value="osoba"
                checked={customerType === "osoba"}
                onChange={() => setCustomerType("osoba")}
                className="mr-2"
              />
              Osoba prywatna
            </label>
            <label className="cursor-pointer">
              <input
                type="radio"
                name="ctype"
                value="firma"
                checked={customerType === "firma"}
                onChange={() => setCustomerType("firma")}
                className="mr-2"
              />
              Firma
            </label>
          </div>

          {/* Pola wspólne */}
          <div className="grid md:grid-cols-2 gap-4">
            <input
              name="name"
              placeholder={customerType === "firma" ? "Nazwa firmy / Imię i nazwisko" : "Imię i nazwisko"}
              value={form.name}
              onChange={handleForm}
              className="border p-2 rounded"
              required
            />
            <input
              name="phone"
              placeholder="Telefon"
              value={form.phone}
              onChange={handleForm}
              className="border p-2 rounded"
              required
            />
            <input
              name="email"
              placeholder="E-mail"
              type="email"
              value={form.email}
              onChange={handleForm}
              className="border p-2 rounded"
              required
            />
            <input
              name="address"
              placeholder="Adres dostawy / odbioru"
              value={form.address}
              onChange={handleForm}
              className="border p-2 rounded"
              required
            />
          </div>

          {/* Pola firmowe */}
          <div className="grid md:grid-cols-2 gap-4 mt-4">
            <input
              name="companyName"
              placeholder="Nazwa firmy"
              value={form.companyName}
              onChange={handleForm}
              className="border p-2 rounded"
              disabled={customerType === "osoba"}
            />
            <input
              name="nip"
              placeholder="NIP"
              value={form.nip}
              onChange={handleForm}
              className="border p-2 rounded"
              disabled={customerType === "osoba"}
            />
          </div>

          {/* Płatność */}
          <div className="mt-4">
            <label className="block mb-2 font-medium">Forma płatności</label>
            <select
              name="payment"
              value={form.payment}
              onChange={handleForm}
              className="border p-2 rounded w-full"
            >
              <option>Gotówka przy odbiorze</option>
              <option>Karta w sklepie</option>
              <option>Blik w sklepie</option>
              <option>Gotówka przy dostawie</option>
              <option>Blik z dostawcą (po ustaleniu)</option>
            </select>
          </div>

          <button
            type="submit"
            className="mt-6 w-full bg-blue-600 text-white py-3 rounded-xl font-semibold hover:bg-blue-700"
          >
            Wyślij zamówienie
          </button>

          <p className="text-sm text-gray-500 mt-3 text-center">
            Zamówienie zostanie przesłane na e-mail obsługi. Płatność zgodnie z wybraną opcją.
          </p>
        </form>
      )}


